mapper.select:
SELECT G.GUIDE_NUM, G.ANSWER_CON , G.TOP_CHATBOT_NUM, G.MENU, NVL(A.CNT,0) AS CNT FROM GUIDE G LEFT OUTER JOIN(SELECT TOP_CHATBOT_NUM, COUNT(TOP_CHATBOT_NUM) AS CNT FROM GUIDE GROUP BY TOP_CHATBOT_NUM)A ON A.TOP_CHATBOT_NUM = G.GUIDE_NUM WHERE DEL = 1
AND G.TOP_CHATBOT_NUM IS NULL
AND G.TOP_CHATBOT_NUM = #{top_num}
