mapper.select:
SELECT C.CAR_PARK_NM,ROUND(B.AVG)AS AVG,B.NM,B.TITLE,B.CON,B.DT, B.REVIEW_NUM,B.CAR_PARK_MAG_NUM FROM ( SELECT M.NM,A.TITLE,A.AVG,A.REVIEW_NUM,A.CAR_PARK_MAG_NUM,A.CON,CASE WHEN TO_CHAR(A.REG_DT,'YY.MM.DD') = TO_CHAR(SYSDATE, 'YY.MM.DD') THEN TO_CHAR(A.REG_DT, 'HH24:MI') ELSE TO_CHAR(A.REG_DT, 'YY.MM.DD') END AS DT FROM( SELECT CAR_PARK_MAG_NUM,MEM_NUM,TITLE,REG_DT,CON,REVIEW_NUM,SUM(CCTV_STAR_SCORE + ENV_STAR_SCORE + FEE_STAR_SCORE + DIS_STAR_SCORE)/4 AS AVG FROM REVIEW GROUP BY REVIEW_NUM,CON,REG_DT,TITLE,MEM_NUM,CAR_PARK_MAG_NUM)A INNER JOIN MEMBER M ON A.MEM_NUM = M.MEM_NUM)B INNER JOIN CAR_PARK C ON B.CAR_PARK_MAG_NUM = C.CAR_PARK_MAG_NUM WHERE C.CAR_PARK_MAG_NUM = #{carparknum}
ORDER BY DT DESC
ORDER BY AVG DESC
