mapper.select:
SELECT C.CAR_PARK_MAG_NUM, C.CAR_PARK_NM, C.CAR_PARK_TP_NM, C.ADDRESS FROM(SELECT CAR_PARK_MAG_NUM,CAR_PARK_NM,ADDRESS, ROW_NUMBER() OVER(ORDER BY CAR_PARK_MAG_NUM DESC) AS RNK, CASE CAR_PARK_TP_NM WHEN 1 THEN '노상' ELSE '노외' END AS CAR_PARK_TP_NM FROM CAR_PARK WHERE 1 = 1
and CAR_PARK_NM LIKE '%' || #{searchTxt} || '%'
and CASE CAR_PARK_TP_NM WHEN 1 THEN '노상' ELSE '노외' END LIKE '%' || #{searchTxt} || '%'
and ADDRESS LIKE '%' || #{searchTxt} || '%'
) C WHERE C.RNK BETWEEN #{start} AND #{end}
