mapper.select:
SELECT Q.QNA_NUM,Q.TITLE,Q.NM,Q.HIT,Q.DT,Q.ADT,Q.CON,Q.ANSWER_CON,Q.PRIVATE FROM(SELECT Q.QNA_NUM,Q.TITLE,M.NM,Q.HIT,Q.CON,Q.ANSWER_CON,Q.PRIVATE, CASE WHEN TO_CHAR(Q.REG_DT,'YY.MM.DD') = TO_CHAR(SYSDATE, 'YY.MM.DD') THEN TO_CHAR(Q.REG_DT, 'HH24:MI') ELSE TO_CHAR(Q.REG_DT, 'YY.MM.DD') END AS DT, CASE WHEN TO_CHAR(Q.ANSWER_DT,'YY.MM.DD') = TO_CHAR(SYSDATE, 'YY.MM.DD') THEN TO_CHAR(Q.ANSWER_DT, 'HH24:MI') ELSE TO_CHAR(Q.ANSWER_DT, 'YY.MM.DD') END AS ADT, ROW_NUMBER() OVER(ORDER BY Q.QNA_NUM DESC) AS RNUM FROM QNA Q INNER JOIN MEMBER M ON Q.MEM_NUM = M.MEM_NUM WHERE Q.DEL = 1
AND Q.TITLE LIKE '%' || #{searchTxt} || '%'
AND M.NM LIKE '%' || #{searchTxt} || '%'
)Q WHERE Q.RNUM BETWEEN #{start} AND #{end}

mapper.update:
		UPDATE QNA SET ANSWER_MEM = #{no},
            ANSWER_CON=#{con},ANSWER_DT=SYSDATE
            WHERE QNA_NUM = #{qna_num}

	UPDATE QNA SET TITLE= #{title},REG_DT=SYSDATE,
	CON=#{con},PRIVATE=#{ckval}
	WHERE QNA_NUM = #{qna_num}
