mapper.select:
SELECT BOARD_NO, TITLE, WRITER, B.WRITE_DATE, B.HIT_CNT FROM (SELECT ROW_NUMBER() OVER(ORDER BY BOARD_NO DESC) AS RNUM, BOARD_NO, TITLE, WRITER, TO_CHAR(WRITE_DATE, 'YYYY/MM/DD') AS WRITE_DATE, HIT_CNT FROM BOARD WHERE DEL_YN = 1
AND TITLE LIKE '%' || #{searchText} || '%'
AND WRITER LIKE '%' || #{searchText} || '%'
AND CONTENTS LIKE '%' || #{searchText} || '%'
) B WHERE B.RNUM BETWEEN #{startCount} AND #{endCount}

mapper.update:
UPDATE BOARD
SET HIT_CNT = HIT_CNT + 1
WHERE BOARD_NO = #{boardNo}

UPDATE BOARD
SET TITLE = #{title}, CONTENTS = #{contents}
WHERE BOARD_NO = #{boardNo}
